mapper.insert:
INSERT INTO WORK (
WCODE, WTITLE, WDATE, WEND, WOPEN, WALARM, ENO,
WSTATUS, DNO, WSTATE, WCONTENT, REQUESTER_ENO, MANAGER_ENO
) VALUES (
#{wcode}, #{wtitle}, #{wdate}, #{wend}, #{wopen}, #{walarm}, #{eno},
#{wstatus}, #{dno}, #{wstate}, #{wcontent}, #{requesterEno},
#{managerEno}
)

mapper.update:
UPDATE WORK
SET WSTATUS = #{status}
WHERE WCODE =
#{wcode}

UPDATE
WORKMANAGER
SET ANSWER = #{answer}
WHERE WCODE = #{wcode} AND ENO =
#{eno}

mapper.select:
SELECT WCODE, WTITLE, WDATE, WEND, WSTATUS, ENO, DNO
FROM WORK
WHERE DNO = #{dno}
